#include <cstdio>
#include <vector>


class Solution
{
    template<typename T>
    using Matrix = std::vector<std::vector<T>>;

    public:
        long long uniquePathsWithObstacles(Matrix<int> &obstacleGrid) {

            if (obstacleGrid.empty()
                || obstacleGrid.front().front() == 1
                || obstacleGrid.back().back() == 1)
            {
                return 0;
            }


            input = &obstacleGrid;
            dp = Matrix<long long>(input->size(), std::vector<long long>((*input)[0].size(), -1));

            return aux(0, 0);
        }


        void showDP() const {
            for (auto &v : dp) {
                for (auto c : v) {
                    printf("%lld ", c);
                }
                printf("\n");
            }
        }

    private:
        long long aux(int i, int j) {
            long long down, right;

            if (dp[i][j] != -1) {
                return dp[i][j];
            }


            // 到达目的地
            if (i == input->size()-1 && j == (*input)[0].size()-1) {
                dp[i][j] = 1;
                return 1;
            }

            // 到达障碍物
            if ((*input)[i][j] == 1) {
                dp[i][j] = 0;
                return 0;
            }

            /*
             * 如果触底，或者到达最右边
             */
            if (i == input->size()-1 || j == (*input)[0].size()-1) {

                /*
                 * 这里不可能到达目的地，因为如果是这样的话，前面就会返回 1 了
                 */
                dp[i][j] = (i == input->size()-1) ? aux(i,j+1) : aux(i+1, j);
                return dp[i][j];
            }

            // 计算向下走一步之后到达终点的总路径数
            down = aux(i+1, j);
            right = aux(i, j+1);

            dp[i][j] = down + right;
            return dp[i][j];
        }


        Matrix<int>        *input;
        Matrix<long long>   dp;
};


